import { Component, AfterViewInit } from '@angular/core';
import * as L from 'leaflet';
import { WebsocketService } from '../service/websocket.service';

@Component({
  selector: 'app-map',
  templateUrl: './map.component.html',
  styleUrls: ['./map.component.css']
})
export class MapComponent implements AfterViewInit {
  private map: any;
  private markerMoving: boolean = false;
  public number: string = '';
  private pos1: any;
  private trail: any[] = [];
  private trailPolyline : any;

  constructor(private websocket: WebsocketService) {}

  ngAfterViewInit(): void {
    this.websocket.getNumber().subscribe((num: string) => {
      this.number = num;
    });
    this.initMap();
  }

  initMap(): void {
    this.map = L.map('map').setView([51.505, -0.09], 13);
    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: '© OpenStreetMap contributors',
      maxZoom: 18
    }).addTo(this.map);

    // Customize the marker to be a big blue dot
    this.pos1 = L.circleMarker([51.5, -0.09], {
      color: 'blue',
      fillColor: 'blue',
      fillOpacity: 1,
      radius: 10 // Adjust the radius as needed
    }).addTo(this.map).bindPopup('Pos 1');

    // Initialize the trailPolyline
    this.trailPolyline = L.polyline([], {
      color: 'gray',
      weight: 2,
      opacity: 0.8
    });

    // Add the trailPolyline to the map
    this.trailPolyline.addTo(this.map);

    setInterval(() => {
      if (this.markerMoving) {
        // Update the marker's position
        const currentLatLng = this.pos1.getLatLng();
        this.pos1.setLatLng([currentLatLng.lat + 0.001, currentLatLng.lng]);

        // Add the current position to the trail
        this.trail.push([currentLatLng.lat, currentLatLng.lng]);

        // Limit the trail length to the last 5 positions
        if (this.trail.length > 30) {
          this.trail.shift(); // Remove the oldest position
        }

        // Update the trailPolyline with the new trail positions
        this.trailPolyline.setLatLngs(this.trail);

        // Center the map on the marker's position
        this.map.setView([currentLatLng.lat, currentLatLng.lng]);
      }
    }, 100);
  }


  toggleMarkerMovement(): void {
    this.markerMoving = !this.markerMoving;
  }

  ngOnDestroy() {
    this.map.remove();
  }
}
